import React from 'react'
import Todolist from './Todolist13.js'
import TodoMainList from './TodoMainList15.js'

class Todo extends React.Component{
 constructor(){
   super()
   this.state={
     todos: Todolist
   }

//added 13 line in part 22
this.handle  = this.handle.bind(this);
 }
//added at part 22(from 14 to 17)
handle(id){

  console.log(id);
}
 render(){

   const TodoItems= this.state.todos.map(
     function(list){
               return(
                 <TodoMainList
                    Key={list.id}
                    listitem={list}
                
                 />
               )
               }
               )


   return(
        <div>{TodoItems}</div>
   )
 }

}

export default Todo
