import React, { Component } from "react";
import Counter from "./counter.js";
export class maincount extends Component {
  constructor(props) {
    super(props);
    this.state = {
      counters: [
        { id: 1, value: 3 },
        { id: 2, value: 0 },
        { id: 3, value: 0 },
        { id: 4, value: 0 },
      ],
    };
  }
  reset = () => {
    const resetlist = this.state.counters.map((c) => {
      c.value = 0;
      return c;
    });
    this.setState({ counters: resetlist });
  };
  handledelete = (id) => {
    const newcounter = this.state.counters.filter((c) => c.id !== id);
    this.setState({ counters: newcounter });
  };
  render() {
    return (
      <div>
        <button className="btn btm-info btn-sm" onClick={this.reset}>
          reset
        </button>
        {this.state.counters.map((counter) => (
          <Counter
            key={counter.id}
            value={counter.value}
            handledelete={this.handledelete}
            id={counter.id}
          >
            <p>counter #{counter.id}</p>
          </Counter>
        ))}
      </div>
    );
  }
}

export default maincount;
