import React, { Component } from "react";
import Todomap from "./todomap";
class app extends Component {
  constructor() {
    super();
    this.state = {
      todos: [
        {
          id: 1,
          title: "todo 1",
          completed: false,
        },
        {
          id: 2,
          title: "todo 2",
          completed: false,
        },
        {
          id: 3,
          title: "todo 3",
          completed: false,
        },
      ],
    };

    this.handle = this.handle.bind();
  }
  handle = (id) => {
    this.setState({
      todos: this.state.todos.map((newtodo) => {
        if (newtodo.id === id) {
          newtodo.completed = !newtodo.completed;
        }
        return newtodo;
      }),
    });
  };
  render() {
    return (
      <div>
        <Todomap todo={this.state.todos} handle={this.handle} del={this.del} />
      </div>
    );
  }
}

export default app;
