import React, { Component } from "react";

export class input extends Component {
  render() {
    return (
      <div>
        <form>
          <input
            type="text"
            name="title"
            placeholder="add a todo"
            value={this.state.title}
            onChange={this.change}
          />
          <input type="submit" value="submit" />
        </form>
      </div>
    );
  }
}

export default input;
