import React, { Component } from "react";
class Forms extends Component {
  constructor() {
    super();
    this.state = {
      firstname: "",
      lastname: "",
    };
    this.handle = this.handle.bind(this);
  }
  handle(event) {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  }
  render() {
    return (
      <form>
        <input type="text" name="firstname" onChange={this.handle} />
        <input type="text" name="lastname" onChange={this.handle} />
      </form>
    );
  }
}
export default Forms;
