import React, { Component } from "react";

export class counter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: this.props.value,
    };
    this.fitvalue = this.fitvalue.bind(this);
    this.setcolor = this.setcolor.bind(this);
  }

  fitvalue() {
    return this.state.counter === 0 ? "zero" : this.state.counter;
  }
  setcolor() {
    let styles = "badge m-2 badge-";
    styles += this.state.counter === 0 ? "warning" : "primary";
    return styles;
  }
  countervalue = () => {
    this.setState({ counter: this.state.counter + 1 });
  };
  render() {
    return (
      <React.Fragment>
        {this.props.children}
        <p className={this.setcolor()}>{this.fitvalue()}</p>
        <button onClick={this.countervalue} className="btn btn-primary btn-sm">
          increment
        </button>
        <button
          onClick={this.decountervalue}
          className="btn btn-success btm-sm"
        >
          decrement
        </button>
        <br /> <br />
      </React.Fragment>
    );
  }
}

export default counter;
