import React, { Component } from "react";
class Memegenerator extends Component {
  constructor() {
    super();
    this.state = {
      toptext: "",
      bottomtext: "",
      randomimage: "http://i.imgflip.com/1bij.jpg",
      allMemeImgs: [],
    };
    this.handle = this.handle.bind(this);
    this.submit = this.submit.bind(this);
  }
  componentDidMount() {
    fetch("https://api.imgflip.com/get_memes")
      .then((response) => response.json())
      .then((response) => {
        const { memes } = response.data;
        console.log(memes);
        this.setState({ allMemeImgs: memes });
      });
  }
  handle(event) {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }
  submit(event) {
    event.preventDefault();
    const random = Math.floor(Math.random() * this.state.allMemeImgs.length);
    const randimg = this.state.allMemeImgs[random].url;
    this.setState({ randomimage: randimg });
  }
  render() {
    return (
      <div>
        <form onSubmit={this.submit}>
          <input
            type="text"
            name="toptext"
            placeholder="toptext"
            value={this.state.toptext}
            onChange={this.handle}
          />
          <input
            type="text"
            name="bottomtext"
            placeholder="bottomtext"
            value={this.state.bottomtext}
            onChange={this.handle}
          />
          <button>generate</button>
        </form>
        <div>
          <p>{this.state.toptext}</p>
          <img src={this.state.randomimage} />
          <p>{this.state.bottomtext}</p>
        </div>
      </div>
    );
  }
}
export default Memegenerator;
