import React from 'react';
function DateGreetings(){
  let date= new Date;
  let hours= date.getHours();
  let greeting;
  if(hours<12){
      greeting = "morning"
  }
  else if(hours>=12 && hours<=17){
      greeting = "afternoon"
    }
  else{
      greeting = "night"
  }

  return (
    <h1>it is about {hours} so have a good {greeting}</h1>
  )
}

export default DateGreetings;
