import React, { Component } from "react";

export class counter extends Component {
  constructor() {
    super();
    this.fitvalue = this.fitvalue.bind(this);
    this.setcolor = this.setcolor.bind(this);
  }

  state = {
    counter: this.props.value,
  };
  fitvalue() {
    return this.state.counter === 0 ? "zero" : this.state.counter;
  }
  setcolor() {
    let styles = "badge m-2 badge-";
    styles += this.state.counter === 0 ? "warning" : "primary";
    return styles;
  }
  countervalue = () => {
    this.setState({ counter: this.state.counter + 1 });
  };
  render() {
    return (
      <React.Fragment>
        {this.props.children}
        <p className={this.setcolor()}>{this.fitvalue()}</p>
        <button onClick={this.countervalue} className="btn btm-primary btm-sm">
          increment
        </button>
        <br /> <br />
      </React.Fragment>
    );
  }
}

export default counter;
