import React, { Component } from "react";

export class counter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: this.props.value,
    };
    this.fitvalue = this.fitvalue.bind(this);
    this.setcolor = this.setcolor.bind(this);
  }

  fitvalue() {
    return this.props.value === 0 ? "zero" : this.state.counter;
  }
  setcolor() {
    let styles = "badge m-2 badge-";
    styles += this.props.value === 0 ? "warning" : "primary";
    return styles;
  }

  render() {
    return (
      <React.Fragment>
        {this.props.children}
        <p className={this.setcolor()}>{this.fitvalue()}</p>
        <button
          onClick={() => this.props.countervalue(this.props.id)}
          className="btn btn-primary btn-sm"
        >
          increment
        </button>
        <button
          onClick={() => this.props.decountervalue(this.props.id)}
          className="btn btn-success btn-sm"
        >
          decrement
        </button>
        <button
          onClick={() => this.props.handledelete(this.props.id)}
          className="btn btn-warning btn-sm"
        >
          delete
        </button>
        <br /> <br />
      </React.Fragment>
    );
  }
}

export default counter;
